/*Halo Express Order*/
#haloExpressOrderLayout {
    & form[data-product-compare] {
        & ul.productList {
            margin: 0;

            &>li {
                padding: 25px;
                margin: 0;
                border-bottom: 1px dotted #dcdcdc;

                &:hover {
                    background: #f8f8f8;

                    & .quickview {
                        opacity: 1;
                        visibility: visible;
                    }
                }
            }
        }
    }

    & article {
        display: -moz-flex;
        display: -webkit-flex;
        display: -ms-flex;
        display: flex;

        @media (max-width: 400px) {
            flex-wrap: wrap;
        }

        & figure {
            width: 128px;
            margin: 0;
            position: relative;

            & .sale-badge {
                width: 42px;
                min-height: 18px;
                text-align: center;
                font-size: 11px;
                font-weight: 700;
                line-height: normal;
                position: absolute;
                z-index: 10;
                top: 0;
                right: 0;
                padding: 0;
                text-transform: uppercase;
                color: white;
                border: 1px solid #e82b1a;
                background: #e82b1a;
                border-radius: 2px;
                display: -webkit-box;
                /* OLD - iOS 6-, Safari 3.1-6 */
                display: -moz-box;
                /* OLD - Firefox 19- (buggy but mostly works) */
                display: -ms-flexbox;
                /* TWEENER - IE 10 */
                display: -webkit-flex;
                /* NEW - Chrome */
                display: flex;
                /* NEW, Spec - Opera 12.1, Firefox 20+ */
                justify-content: center;
                align-items: center;
            }

            & .quickview {
                background: rgba(0, 0, 0, .8);
                color: #fff;
                height: 22px;
                border-radius: 3px;
                width: 100%;
                text-align: center;
                text-transform: uppercase;
                font-size: 11px;
                opacity: 0;
                visibility: hidden;
                letter-spacing: 0.05em;
                font-weight: 700;
                display: -webkit-box;
                /* OLD - iOS 6-, Safari 3.1-6 */
                display: -moz-box;
                /* OLD - Firefox 19- (buggy but mostly works) */
                display: -ms-flexbox;
                /* TWEENER - IE 10 */
                display: -webkit-flex;
                /* NEW - Chrome */
                display: flex;
                /* NEW, Spec - Opera 12.1, Firefox 20+ */
                justify-content: center;
                align-items: center;
            }
        }

        & .expressItem-body {
            width: calc(100% - 100px);
            padding-left: 30px;

            @media (max-width: 400px) {
                width: 100%;
                padding-left: 0;
            }

            & .expressItem-content {
                display: -moz-flex;
                display: -webkit-flex;
                display: -ms-flex;
                display: flex;

                @media (max-width: 767px) {
                    flex-wrap: wrap;
                }

                & .expressItem-details {
                    width: 35%;
                    padding-top: 15px;

                    @media (max-width: 1199px) {
                        width: 30%;
                    }

                    @media (max-width: 800px) {
                        width: 40%;
                    }

                    @media (max-width: 767px) {
                        width: 100%;
                    }

                    & .expressItem-title {
                        margin: 0;
                        padding: 0 0 10px;
                        font-size: 14px;
                        font-weight: 600;
                        letter-spacing: 0;
                        line-height: 20px;
                    }

                    & .expressItem-sku {
                        font-size: 12px;
                        padding-bottom: 8px;

                        & span {
                            padding-right: 10px;
                            font-weight: 500;
                        }
                    }

                    & .expressItem-rating {
                        margin: 0;
                        padding: 0 0 6px;
                    }
                }

                & .expressItem-actions {
                    width: 65%;
                    display: -moz-flex;
                    display: -webkit-flex;
                    display: -ms-flex;
                    display: flex;

                    @media (max-width: 1199px) {
                        width: 70%;
                    }

                    @media (max-width: 800px) {
                        width: 60%;
                    }

                    @media (max-width: 767px) {
                        width: 100%;
                        flex-wrap: wrap;
                    }

                    & .expressItem-price {
                        width: 240px;
                        text-align: center;
                        padding-top: 8px;
                        font-size: 14px;
                        font-weight: 600;
                        padding-left: 10px;
                        padding-right: 10px;

                        @media (max-width: 991px) {
                            width: 140px;
                        }

                        @media (max-width: 767px) {
                            width: 100%;
                            text-align: left;
                            padding-left: 0;
                            padding-bottom: 15px;
                        }

                        & span {
                            padding-left: 5px;
                            padding-right: 5px;

                            &~span {
                                color: #e82b1a;
                            }
                        }
                    }

                    & .addtocart-box {
                        width: calc(100% - 200px);
                        display: -moz-flex;
                        display: -webkit-flex;
                        display: -ms-flex;
                        display: flex;
                        justify-content: space-between;
                        align-items: start;

                        @media (max-width: 991px) {
                            width: calc(100% - 140px);
                        }

                        @media (max-width: 800px) {
                            flex-wrap: wrap;
                        }

                        @media (max-width: 767px) {
                            width: 100%;
                        }

                        &>.form-field {
                            width: 170px;
                            padding: 8px 15px 0 15px;
                            text-align: center;

                            @media (max-width: 800px) {
                                width: 100%;
                            }
                        }

                        &>div[class*="data-product-option-"] {
                            width: calc(100% - 170px);
                            max-width: 220px;
                            display: -moz-flex;
                            display: -webkit-flex;
                            display: -ms-flex;
                            display: flex;
                            flex-wrap: wrap;
                            justify-content: flex-end;
                            align-items: start;

                            @media (max-width: 800px) {
                                width: 100%;
                            }

                            &>a {
                                order: 2;
                                width: 100%;
                                margin: 0;
                                text-transform: uppercase;
                                font-size: 13px;
                                font-weight: 700;

                                &:hover {
                                    background-color: #E12026;
                                    border-color: #E12026;
                                    color: #fff;
                                }
                            }

                            &>div[data-product-option-change] {
                                width: 100%;
                                position: relative;
                                z-index: 1;

                                &>a[data-reveal-id] {
                                    display: none;
                                }

                                &>.form-field {
                                    margin-bottom: 15px;

                                    & .form-option {
                                        margin-right: 7px;
                                    }

                                    & .form-option:last-of-type {
                                        margin-right: 0;
                                    }
                                }
                            }
                        }

                        &>.alert {
                            width: 100%;
                            max-width: 220px;
                            border: 1px solid #dcdcdc;
                            background: #fff;
                            height: 36px;
                            padding: 7px 0px;
                            font-size: 13px;
                            text-transform: uppercase;
                            color: #aaaaaa;
                            font-weight: 700;
                            border-radius: 3px;
                        }

                        &>a {
                            width: calc(100% - 170px);
                            max-width: 220px;
                            text-transform: uppercase;
                            font-size: 13px;
                            font-weight: 700;

                            &:hover {
                                background-color: #E12026;
                                border-color: #E12026;
                                color: #fff;
                            }

                            @media (max-width: 800px) {
                                width: 100%;
                            }
                        }

                        & .form-increment {
                            & .form-input--incrementTotal {
                                margin: 0 -3px;
                                display: inline-block;
                                font-size: 14px;
                                height: auto;
                                padding: 0;
                                text-align: center;
                                vertical-align: middle;
                                width: 2.91667rem;
                                min-width: 50px;
                                height: 36px;
                                border: 1px solid #cbcbcb;
                                border-width: 1px 0;
                                border-radius: 0;
                            }

                            & button {
                                padding: 7px;
                                background: #ffffff;
                                width: 36px;
                                height: 36px;
                                border-radius: 0;

                                & .icon {
                                    width: 100%;
                                    height: 100%;
                                }
                            }
                        }
                    }
                }
            }
        }
    }

    & .halo-express-bottom {
        padding: 30px 0;
        display: -moz-flex;
        display: -webkit-flex;
        display: -ms-flex;
        display: flex;
        justify-content: space-between;
        align-items: center;

        @media (max-width: 767px) {
            justify-content: center;
            flex-wrap: wrap;
            padding-top: 25px;
        }

        &>.product-pagination {
            width: 70%;
            padding: 0;
            margin: 0;
            justify-content: center;

            @media (max-width: 767px) {
                width: 100%;
            }

            &>.pagination {
                width: auto;

                @media (max-width: 767px) {
                    width: 100%;
                }
            }
        }

        &>a {
            min-width: 270px;
            background-color: #e82b1a;
            border-color: #e82b1a;
            margin: 0;
            height: 45px;
            line-height: 1.6em;
            font-weight: 700;
            text-transform: uppercase;
            font-size: 13px;
            padding-bottom: 32px;
            color: #fff;

            @media (max-width: 991px) {
                min-width: 210px;
            }

            @media (max-width: 767px) {
                order: -1;
                margin-bottom: 25px;
                width: 100%;
            }

            &:hover {
                background-color: #E12026;
                border-color: #E12026;
                color: #fff;
            }
        }
    }

    .productList {
        float: unset;
    }

    .actionBar,
    .actionBar-section {
        float: right;
    }

    .pagination-list {
        margin: 0 30px 0 0;
        text-align: left;

        @media (max-width: 767px) {
            margin: 0;
            text-align: center;
        }

        .pagination-item {
            margin-right: 5px;
            margin-left: 0;

            @media (max-width: 767px) {
                padding-left: 8px;
                padding-right: 8px;
                margin-bottom: 5px;
            }
        }


    }

    .productView-info {
        clear: both;
    }

    .productView-info-name {
        color: #757575;
        float: left;
        font-weight: 400;
        margin: 0 12px 0 0;
        padding: 10px 0;
        margin-top: 0 !important;
    }

    .productView-info-value {
        margin: 0;
        padding: 10px 0;
        float: left;
    }

    .productView-price {
        font-size: 20px;
    }
}

.haloExpressCartAdded {
    padding: 25px 0 0 0;
    display: -moz-flex;
    display: -webkit-flex;
    display: -ms-flex;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;

    & .previewCartAction-checkout {
        width: 300px;
        padding: 15px;
        background: #f6f6f6;
        text-align: center;

        @media (max-width: 767px) {
            width: 100%;
        }

        & h3 {
            margin: 10px 0 15px;
            font-size: 18px;
            letter-spacing: 0;
        }

        & .cart-totals {
            width: 100%;

            & li {
                & div {
                    font-size: 14px;
                    font-family: montserrat, sans-serif !important;
                }
            }
        }

        &>a {
            width: 100%;
            max-width: 100%;
            margin-bottom: 0;
            margin-top: 10px;

            @media (max-width: 767px) {
                margin-bottom: 5px;
            }
        }
    }

    & .previewCartAction-viewCart {
        width: calc(100% - 300px);
        padding-right: 15px;
        text-align: left;

        @media (max-width: 767px) {
            width: 100%;
        }

        & h4 {
            font-size: 18px;
            margin: 0;
            padding-bottom: 15px;
            letter-spacing: 0;
            line-height: 20px;
        }

        & .previewCartList {
            margin-bottom: 15px;
            overflow-y: auto;
            max-height: 180px;

            & .previewCartItem {
                padding: 15px 0;
                display: -moz-flex;
                display: -webkit--flex;
                display: -ms--flex;
                display: flex;

                & .previewCartItem-name {
                    text-transform: none;
                    margin: 0;
                    font-size: 20px;
                    line-height: 20px;
                    font-weight: 600;
                    letter-spacing: 0;
                    text-align: left;
                }

                & .definitionList {
                    clear: both;
                }
            }
        }

        &>p {
            margin: 0;
        }

        & #modal_close {
            background: #ffffff;
            border: 1px solid #000;
            border-radius: 3px;
            display: inline-flex;
            align-items: center;
            font-size: 13px;
            font-weight: 700;
            height: 36px;
            line-height: initial;
            text-transform: uppercase;
            padding: 0 25px;

            &:hover {
                background: #000;
                color: #fff;
            }
        }
    }
}

.modal-express-order {
    .modal-header-title {
        text-transform: uppercase;
        margin-top: -20px;
        margin-bottom: 12px;
    }

    .productView-info-value {
        margin-bottom: 15px;
    }
}

.button--icon svg {
    width: 15px;
    height: 15px;
}

.form-increment .button {
    position: unset;
    border-style: solid;
    border-width: 1px;
    border-color: #cbcbcb;
}

@media(max-width: 900px) {
    #haloExpressOrderLayout article {
        .expressItem-body {
            .expressItem-content {
                .expressItem-actions {
                    .addtocart-box>.form-field {
                        padding: 0;
                        text-align: unset;
                    }

                    .expressItem-price {
                        padding-left: 0;
                        padding-top: 0;

                        .price-section {
                            display: flex;
                            font-size: 16px !important;
                            justify-content: center;
                        }
                    }
                }
            }
        }
    }

    .form-field {
        margin: 0 0 1rem 0;
    }


    .navUser {
        position: unset;
        transform: unset;
    }

    .modal> :first-child {
        margin-top: -11px;
        right: 4px;
        font-size: 40px;
    }

    .cart-item-image {
        width: 100%;
    }

    .previewCartItem-content {
        padding-left: 0.75rem;


    }

    .modal-body.quickView h4 {
        font-size: 20px;
        text-align: center;
    }

    .cart-totals {
        text-align: center;

        .cart-total {
            display: flex;
            align-items: center;
            justify-content: center;

            .cart-total-label {
                width: unset;
            }
        }
    }



    #haloExpressOrderLayout article .expressItem-body .expressItem-content .expressItem-actions .addtocart-box {
        padding-top: 0;
    }

    #haloExpressOrderLayout article .expressItem-body .expressItem-content .expressItem-actions .addtocart-box>div[class*="data-product-option-"] {
        padding-top: 0;
    }
}

@media(min-width: 1025px) {
    .add-to-cart {
        padding: 12px 0;
    }

    .first-herolink {
        padding: 12px;
        margin-left: 0 !important;
    }

    .headerLeft.logo-active {
        padding-top: 8px;
    }

    .headerCenter {
        display: flex;
    }

    .main-nav ul {
        display: flex;

        li {
            a.navPages-action {
                font-size: 14px;
                padding: 12px;
            }
        }
    }

    .modal-body.quickView h4 {
        font-size: 20px;
        text-align: center;
    }

    #haloExpressOrderLayout article .expressItem-body .expressItem-content .expressItem-actions .addtocart-box>div[class*="data-product-option-"]>a {
        padding: 12px;
    }

}

@media(min-width: 820px) and (max-width: 1023px) {
    #haloExpressOrderLayout {
        article {
            .expressItem-body {
                .expressItem-content {
                    .expressItem-actions {
                        .addtocart-box {
                            flex-wrap: wrap;

                            &>a {
                                width: 100%;
                                padding: 12px;

                            }

                            &.expressItem-price {
                                width: 100%;
                            }

                            &>div[class*="data-product-option-"] {
                                width: 100%;
                            }

                            &>.form-field {
                                width: 100%;
                            }

                            &>div[class*="data-product-option-"]>a {
                                padding: 12px;
                            }

                        }
                    }

                }

            }

        }
    }

    #haloExpressOrderLayout article .expressItem-body .expressItem-content .expressItem-actions .expressItem-price {
        width: 240px;
    }

}

@media(max-width: 767px) {

    #haloExpressOrderLayout article {
        display: block;

        img {
            width: 50%;
        }

        figure {
            width: 100%;
            text-align: center;
        }

        .expressItem-body {
            width: 100%;
            text-align: center;
            padding-left: 0;

            .expressItem-content {
                display: block;

                .expressItem-actions {
                    display: block;

                    .addtocart-box {
                        display: block;

                        &>.form-field {
                            text-align: center;
                        }

                        &>div[class*="data-product-option-"] {
                            max-width: unset;
                        }

                        &>a {
                            max-width: unset;
                            margin-bottom: 0;
                        }
                    }
                }

                .expressItem-details .expressItem-title {
                    font-size: 18px;
                }
            }
        }
    }


    .haloExpressCartAdded .previewCartAction-viewCart #modal_close {
        width: 100%;
        justify-content: center;
    }

    .modal-body.quickView h4 {
        font-size: 15px;
        text-align: center;
    }

    .price-section {
        justify-content: center;
    }


}

.form-increment .button[data-action="dec"]:before {
    display: none;
}

.form-increment .button[data-action="inc"]:before {
    display: none;
}
